        [HttpPost]
        //[Route("downloadzip")]
        public async Task<IActionResult> DownloadTheZipFile()
        {
            List<string> files = new List<string>
        {
         Path.Combine("ZipProgress","file1.txt"),
         Path.Combine("ZipProgress","file2.txt"),
        };
            //zipear archivo
            var tempFile = Path.GetTempFileName();

            using (FileStream zipFile = System.IO.File.Create(tempFile))
            using (var zipArchive = new ZipArchive(zipFile, ZipArchiveMode.Create))
            {
                foreach (var file in files)
                {
                    zipArchive.CreateEntryFromFile(file, Path.GetFileName(file));
                }
            }

            var stream = new FileStream(tempFile, FileMode.Open);
            return File(stream, "application/zip", "my.zip");

        }

